import { useEffect, useState } from "react";

const TopProducts = () => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [sortBy, setSortBy] = useState('sales');

  useEffect(() => {
    fetch("http://localhost:5000/api/products/pricing")
      .then((res) => res.json())
      .then((data) => setProducts(data));
    
    fetch("http://localhost:5000/api/categories")
      .then((res) => res.json())
      .then((data) => setCategories(data));
  }, []);

  const filteredProducts = products.filter(product => 
    selectedCategory === 'All' || product.category === selectedCategory
  );

  const sortedProducts = [...filteredProducts].sort((a, b) => {
    if (sortBy === 'sales') return b.sales - a.sales;
    if (sortBy === 'clicks') return b.clicks - a.clicks;
    if (sortBy === 'price') return b.price - a.price;
    return 0;
  });

  const getRankColor = (index) => {
    const colors = [
      'from-yellow-400 to-orange-500', // Gold
      'from-gray-300 to-gray-400',     // Silver
      'from-amber-600 to-amber-700',   // Bronze
      'from-indigo-500 to-indigo-600', // 4th
      'from-purple-500 to-purple-600'  // 5th
    ];
    return colors[index] || 'from-gray-400 to-gray-500';
  };

  const getCategoryIcon = (category) => {
    const icons = {
      'Electronics': 'üì±',
      'Audio': 'üéµ',
      'Computers': 'üíª',
      'Wearables': '‚åö',
      'Photography': 'üì∏',
      'Accessories': 'üîß',
      'Gaming': 'üéÆ',
      'Networking': 'üåê',
      'Storage': 'üíæ',
      'Smart Home': 'üè†',
      'Displays': 'üñ•Ô∏è',
      'Appliances': 'üè†'
    };
    return icons[category] || 'üì¶';
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-100">
      <div className="p-6 space-y-8">
        {/* Header */}
        <div className="text-center">
          <h1 className="text-4xl font-bold bg-gradient-to-r from-indigo-600 to-purple-600 bg-clip-text text-transparent mb-4">
            üèÜ Top Products
          </h1>
          <p className="text-gray-600 text-lg">Discover our best-performing products across all categories</p>
        </div>

        {/* Filters */}
        <div className="bg-white rounded-2xl p-6 shadow-xl border border-gray-100">
          <div className="flex flex-col md:flex-row gap-4 items-center justify-between">
            <div className="flex flex-wrap gap-2">
              <button
                onClick={() => setSelectedCategory('All')}
                className={`px-4 py-2 rounded-lg font-medium transition-all duration-300 ${
                  selectedCategory === 'All'
                    ? 'bg-gradient-to-r from-indigo-500 to-purple-500 text-white shadow-lg'
                    : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                }`}
              >
                All Categories
              </button>
              {categories.map((category) => (
                <button
                  key={category.category}
                  onClick={() => setSelectedCategory(category.category)}
                  className={`px-4 py-2 rounded-lg font-medium transition-all duration-300 ${
                    selectedCategory === category.category
                      ? 'bg-gradient-to-r from-indigo-500 to-purple-500 text-white shadow-lg'
                      : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                  }`}
                >
                  {getCategoryIcon(category.category)} {category.category}
                </button>
              ))}
            </div>

            <div className="flex items-center space-x-2">
              <label className="text-sm font-medium text-gray-700">Sort by:</label>
              <select
                value={sortBy}
                onChange={(e) => setSortBy(e.target.value)}
                className="px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
              >
                <option value="sales">Sales</option>
                <option value="clicks">Clicks</option>
                <option value="price">Price</option>
              </select>
            </div>
          </div>
        </div>

        {/* Top 5 Podium */}
        <div className="grid grid-cols-1 md:grid-cols-5 gap-6">
          {sortedProducts.slice(0, 5).map((product, index) => (
            <div 
              key={index}
              className={`bg-gradient-to-br ${getRankColor(index)} rounded-2xl p-6 text-white shadow-xl transform hover:scale-105 transition-all duration-300 ${
                index === 0 ? 'md:col-span-2 md:row-span-2' : ''
              }`}
            >
              <div className="text-center">
                <div className="text-6xl mb-4">
                  {index === 0 ? 'ü•á' : index === 1 ? 'ü•à' : index === 2 ? 'ü•â' : `#${index + 1}`}
                </div>
                <h3 className="text-xl font-bold mb-2">{product.product_name}</h3>
                <div className="space-y-2">
                  <div className="bg-white/20 rounded-lg p-2">
                    <p className="text-sm opacity-90">Sales</p>
                    <p className="text-2xl font-bold">{product.sales}</p>
                  </div>
                  <div className="bg-white/20 rounded-lg p-2">
                    <p className="text-sm opacity-90">Clicks</p>
                    <p className="text-xl font-bold">{product.clicks}</p>
                  </div>
                  <div className="bg-white/20 rounded-lg p-2">
                    <p className="text-sm opacity-90">Price</p>
                    <p className="text-lg font-bold">${product.price}</p>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Category Stats */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {categories.map((category, index) => (
            <div 
              key={index}
              className="bg-white rounded-2xl p-6 shadow-xl border border-gray-100 hover:shadow-2xl transition-all duration-300 transform hover:scale-105"
            >
              <div className="flex items-center justify-between mb-4">
                <div className="flex items-center space-x-3">
                  <div className="text-3xl">{getCategoryIcon(category.category)}</div>
                  <h3 className="text-xl font-bold text-gray-800">{category.category}</h3>
                </div>
                <div className="bg-gradient-to-r from-indigo-500 to-purple-500 text-white px-3 py-1 rounded-full text-sm font-medium">
                  {category.product_count} products
                </div>
              </div>
              
              <div className="space-y-3">
                <div className="flex justify-between items-center">
                  <span className="text-gray-600">Total Sales</span>
                  <span className="font-bold text-green-600">{category.total_sales}</span>
                </div>
                <div className="flex justify-between items-center">
                  <span className="text-gray-600">Total Clicks</span>
                  <span className="font-bold text-blue-600">{category.total_clicks}</span>
                </div>
                <div className="flex justify-between items-center">
                  <span className="text-gray-600">Avg Price</span>
                  <span className="font-bold text-purple-600">${category.avg_price}</span>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* All Products Grid */}
        <div className="bg-white rounded-2xl p-6 shadow-xl border border-gray-100">
          <h3 className="text-2xl font-bold text-gray-800 mb-6">All Products ({sortedProducts.length})</h3>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
            {sortedProducts.map((product, index) => (
              <div 
                key={index}
                className="bg-gradient-to-br from-white to-gray-50 rounded-xl p-6 shadow-lg border border-gray-100 hover:shadow-xl transition-all duration-300 transform hover:scale-105 hover:-translate-y-1"
              >
                <div className="flex items-center justify-between mb-4">
                  <div className="text-2xl">{getCategoryIcon(product.category)}</div>
                  <div className="bg-gradient-to-r from-indigo-500 to-purple-500 text-white px-2 py-1 rounded-full text-xs font-medium">
                    #{index + 1}
                  </div>
                </div>
                
                <h4 className="font-bold text-gray-800 mb-2 truncate" title={product.product_name}>
                  {product.product_name}
                </h4>
                
                <div className="space-y-2">
                  <div className="flex justify-between items-center">
                    <span className="text-sm text-gray-600">Sales</span>
                    <span className="font-bold text-green-600">{product.sales}</span>
                  </div>
                  <div className="flex justify-between items-center">
                    <span className="text-sm text-gray-600">Clicks</span>
                    <span className="font-bold text-blue-600">{product.clicks}</span>
                  </div>
                  <div className="flex justify-between items-center">
                    <span className="text-sm text-gray-600">Price</span>
                    <span className="font-bold text-purple-600">${product.price}</span>
                  </div>
                  <div className="flex justify-between items-center">
                    <span className="text-sm text-gray-600">Category</span>
                    <span className="text-xs bg-gray-100 px-2 py-1 rounded-full">{product.category}</span>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default TopProducts;
