import { useEffect, useMemo, useState } from "react";
import { LineChart, Line, BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer, CartesianGrid, PieChart, Pie, Cell, Legend } from "recharts";

export default function App() {
  const [products, setProducts] = useState([]);
  const [messageSent, setMessageSent] = useState(false);
  const [summary, setSummary] = useState(null);

  useEffect(() => {
    fetch("http://localhost:5000/api/products")
      .then((res) => res.json())
      .then((data) => setProducts(data));
    fetch("http://localhost:5000/api/summary")
      .then((res) => res.json())
      .then((data) => setSummary(data));
  }, []);

  const sendMessage = () => {
    fetch("http://localhost:5000/send-alert", { method: "POST", headers: { "Content-Type": "application/json" }, body: JSON.stringify({ to: undefined }) })
      .then(() => setMessageSent(true));
  };

  const colors = ["#6366F1", "#22C55E", "#F59E0B", "#EF4444", "#06B6D4", "#A855F7"];
  const salesDistribution = useMemo(() => {
    const top = [...products].slice(0, 6);
    return top.map((p, i) => ({ name: p.product_name, value: p.sales, fill: colors[i % colors.length] }));
  }, [products]);

  return (
    <div className="p-6 grid gap-6">
      {/* KPI header */}
      {summary && (
        <div className="grid grid-cols-4 gap-4">
          <div className="rounded-xl p-4 bg-white shadow border">
            <div className="text-xs text-gray-500">Total Sales</div>
            <div className="text-2xl font-bold">{summary.total_sales}</div>
          </div>
          <div className="rounded-xl p-4 bg-white shadow border">
            <div className="text-xs text-gray-500">Total Clicks</div>
            <div className="text-2xl font-bold">{summary.total_clicks}</div>
          </div>
          <div className="rounded-xl p-4 bg-white shadow border">
            <div className="text-xs text-gray-500">Avg Sales</div>
            <div className="text-2xl font-bold">{summary.avg_sales}</div>
          </div>
          <div className="rounded-xl p-4 bg-white shadow border">
            <div className="text-xs text-gray-500">Top Product</div>
            <div className="text-2xl font-bold truncate" title={summary.top_product.product_name}>{summary.top_product.product_name}</div>
          </div>
        </div>
      )}

      {/* Top Section */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {/* Line Chart */}
        <div className="rounded-xl p-4 bg-white shadow border">
          <div className="font-semibold mb-2">Clicks Trend</div>
          <div style={{ width: '100%', height: 220 }}>
            <ResponsiveContainer>
              <LineChart data={products} margin={{ left: 8, right: 8, top: 8, bottom: 8 }}>
                <CartesianGrid strokeDasharray="3 3" />
                <Line type="monotone" dataKey="clicks" stroke="#6366F1" strokeWidth={2} dot={false} />
                <XAxis dataKey="product_name" hide />
                <YAxis />
                <Tooltip />
              </LineChart>
            </ResponsiveContainer>
          </div>
        </div>

        {/* Bar Chart */}
        <div className="rounded-xl p-4 bg-white shadow border">
          <div className="font-semibold mb-2">Sales by Product</div>
          <div style={{ width: '100%', height: 220 }}>
            <ResponsiveContainer>
              <BarChart data={products.slice(0, 12)} margin={{ left: 8, right: 8, top: 8, bottom: 8 }}>
                <CartesianGrid strokeDasharray="3 3" />
                <Bar dataKey="sales" fill="#22C55E" radius={[4, 4, 0, 0]} />
                <XAxis dataKey="product_name" tick={{ fontSize: 10 }} interval={0} angle={-35} textAnchor="end" height={60} />
                <YAxis />
                <Tooltip />
              </BarChart>
            </ResponsiveContainer>
          </div>
        </div>

        {/* Donut: Sales distribution */}
        <div className="rounded-xl p-4 bg-white shadow border">
          <div className="font-semibold mb-2">Top Sales Distribution</div>
          <div style={{ width: '100%', height: 220 }}>
            <ResponsiveContainer>
              <PieChart>
                <Pie data={salesDistribution} dataKey="value" nameKey="name" innerRadius={50} outerRadius={80}>
                  {salesDistribution.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={entry.fill} />
                  ))}
                </Pie>
                <Legend verticalAlign="bottom" height={36} />
                <Tooltip />
              </PieChart>
            </ResponsiveContainer>
          </div>
        </div>
      </div>

      {/* Top Products */}
      <div className="rounded-xl p-4 bg-white shadow border">
          <h2 className="font-bold text-lg mb-2">Top Products</h2>
          <ul className="space-y-2">
            {products.slice(0, 5).map((p, i) => (
              <li key={i} className="text-sm">{i+1}. {p.product_name}</li>
            ))}
          </ul>
        </div>

      {/* Middle Section */}
      <div className="flex justify-center">
        <button
          onClick={sendMessage}
          className="bg-indigo-600 hover:bg-indigo-700 transition text-white px-4 py-2 rounded-lg shadow"
        >
          {messageSent ? "Message Sent ✅" : "Send Alert to Customers"}
        </button>
      </div>

      {/* Bottom Section */}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-4">
        {products.map((p, i) => (
          <div key={i} className="border rounded-xl p-4 shadow text-center bg-white">
            <h3 className="font-semibold truncate" title={p.product_name}>{p.product_name}</h3>
            <p className="text-gray-600">Sales: {p.sales}</p>
            <p className="text-gray-400 text-sm">Clicks: {p.clicks}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
