import { useEffect, useState } from "react";
import { LineChart, Line, BarChart, Bar, XAxis, YAxis, Tooltip } from "recharts";

export default function App() {
  const [products, setProducts] = useState([]);
  const [messageSent, setMessageSent] = useState(false);

  useEffect(() => {
    fetch("http://localhost:5000/api/products")
      .then((res) => res.json())
      .then((data) => setProducts(data));
  }, []);

  const sendMessage = () => {
    fetch("http://localhost:5000/send-alert", { method: "POST" })
      .then(() => setMessageSent(true));
  };

  return (
    <div className="p-6 grid gap-6">
      {/* Top Section */}
      <div className="grid grid-cols-3 gap-4">
        {/* Line Chart */}
        <LineChart width={300} height={200} data={products}>
          <Line type="monotone" dataKey="clicks" stroke="#8884d8" />
          <XAxis dataKey="product_name" />
          <YAxis />
          <Tooltip />
        </LineChart>

        {/* Bar Chart */}
        <BarChart width={300} height={200} data={products}>
          <Bar dataKey="sales" fill="#82ca9d" />
          <XAxis dataKey="product_name" />
          <YAxis />
          <Tooltip />
        </BarChart>

        {/* Top Products */}
        <div className="border p-4 rounded-xl shadow">
          <h2 className="font-bold text-lg mb-2">Top Products</h2>
          <ul className="space-y-2">
            {products.slice(0, 5).map((p, i) => (
              <li key={i} className="text-sm">{i+1}. {p.product_name}</li>
            ))}
          </ul>
        </div>
      </div>

      {/* Middle Section */}
      <div className="flex justify-center">
        <button
          onClick={sendMessage}
          className="bg-blue-500 text-white px-4 py-2 rounded-lg shadow"
        >
          {messageSent ? "Message Sent ✅" : "Send Alert to Customers"}
        </button>
      </div>

      {/* Bottom Section */}
      <div className="grid grid-cols-4 gap-4">
        {products.map((p, i) => (
          <div key={i} className="border rounded-xl p-4 shadow text-center">
            <h3 className="font-semibold">{p.product_name}</h3>
            <p className="text-gray-600">Sales: {p.sales}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
